INSERT INTO trad (key, lang, translation) VALUES ('gallery_francois_desc', 'fr', 'La galerie François- Ier est une grande galerie d''apparat située au premier étage du château royal de Fontainebleau.');
INSERT INTO trad (key, lang, translation) VALUES ('gallery_francois_desc', 'en', 'The François I gallery is a large ceremonial gallery located on the first floor of the royal castle of Fontainebleau.');
INSERT INTO trad (key, lang, translation) VALUES ('gallery_francois_desc', 'es', 'La galería de Francisco I es una gran galería ceremonial ubicada en el primer piso del castillo real de Fontainebleau.');
