INSERT INTO trad (key, lang, translation) VALUES ('titre_principal_jardins', 'fr', 'Jardins') ON CONFLICT (key, lang) DO NOTHING;
INSERT INTO trad (key, lang, translation) VALUES ('etang_carpes', 'fr', 'L''Étang aux carpes') ON CONFLICT (key, lang) DO NOTHING;
INSERT INTO trad (key, lang, translation) VALUES ('etang_carpes_text', 'fr', 'Ouvrant sur le midi, l''étang aux carpes doit son nom aux fameuses carpes dont la présence à Fontainebleau est attestée depuis Henri IV.') ON CONFLICT (key, lang) DO NOTHING;
INSERT INTO trad (key, lang, translation) VALUES ('grotte_pins', 'fr', 'La grotte des Pins') ON CONFLICT (key, lang) DO NOTHING;
INSERT INTO trad (key, lang, translation) VALUES ('grotte_pins_text', 'fr', 'Exemple précoce de grotte artificielle en France, la « grotte des pins » termine l''extrémité de l''aile Louis XV construite à la place de l''aile de la galerie d''Ulysse.') ON CONFLICT (key, lang) DO NOTHING;
INSERT INTO trad (key, lang, translation) VALUES ('grand_parterre', 'fr', 'Le Grand Parterre') ON CONFLICT (key, lang) DO NOTHING;
INSERT INTO trad (key, lang, translation) VALUES ('grand_parterre_text', 'fr', 'La création du Grand Parterre de 1660 à 1664 - le plus vaste d''Europe, avec ses 14 hectares - par André Le Nôtre et Louis Le Vau, témoigne de la clarification de l''espace voulue par Louis XIV à Fontainebleau.') ON CONFLICT (key, lang) DO NOTHING;
INSERT INTO trad (key, lang, translation) VALUES ('parc', 'fr', 'Le Parc') ON CONFLICT (key, lang) DO NOTHING;
INSERT INTO trad (key, lang, translation) VALUES ('parc_text', 'fr', 'Étendu au-delà du dénivelé du bassin des Cascades, le parc marquait autrefois - à l''est - les limites du domaine royal. Depuis le village d''Avon, il était traversé par la principale voie d''accès au château. Sa configuration actuelle, l''organisation du réseau de cascatelles et d''allées en étoile, résulte de la création, sous Henri IV, d''un grand canal long de près de 1200 mètres (1606-1609) pour 40 mètres de large. Planté initialement de plus de soixante mille arbres où croissaient les rangées de peupliers blancs, les chênes et les arbres fruitiers, ce « parc fermé » et son canal étaient la fierté du roi, qui en suivit le chantier. En 1609, il fallut plus d''une semaine pour le remplir et, dès l''automne, le roi y naviguait.') ON CONFLICT (key, lang) DO NOTHING;
INSERT INTO trad (key, lang, translation) VALUES ('cour_offices', 'fr', 'La cour des Offices/quartier Henri IV') ON CONFLICT (key, lang) DO NOTHING;
INSERT INTO trad (key, lang, translation) VALUES ('cour_offices_text', 'fr', 'Vaste ensemble formé par trois ailes en grès, briques et moellons enduits, cette avant-cour démontre l''ambition d''Henri IV de créer un nouvel accès au château, par la ville.') ON CONFLICT (key, lang) DO NOTHING;
INSERT INTO trad (key, lang, translation) VALUES ('porte_baptistere', 'fr', 'La porte du Baptistère') ON CONFLICT (key, lang) DO NOTHING;
INSERT INTO trad (key, lang, translation) VALUES ('porte_baptistere_text', 'fr', 'C''est Henri IV, grand roi bâtisseur, qui fit élever, vers 1606, cette porte triomphale surmontée d''un dôme, offrant à la cour ovale une ouverture à l''Est.') ON CONFLICT (key, lang) DO NOTHING;
INSERT INTO trad (key, lang, translation) VALUES ('horaires_titre', 'fr', 'Voici les horaires :') ON CONFLICT (key, lang) DO NOTHING;
INSERT INTO trad (key, lang, translation) VALUES ('horaires_lien', 'fr', 'Lien vers les horaires') ON CONFLICT (key, lang) DO NOTHING;
INSERT INTO trad (key, lang, translation) VALUES ('titre_principal_jardins', 'en', 'Gardens') ON CONFLICT (key, lang) DO NOTHING;
INSERT INTO trad (key, lang, translation) VALUES ('etang_carpes', 'en', 'The Carp Pond') ON CONFLICT (key, lang) DO NOTHING;
INSERT INTO trad (key, lang, translation) VALUES ('etang_carpes_text', 'en', 'Opening to the south, the carp pond owes its name to the famous carp that have been present at Fontainebleau since Henri IV.') ON CONFLICT (key, lang) DO NOTHING;
INSERT INTO trad (key, lang, translation) VALUES ('grotte_pins', 'en', 'The Pine Grotto') ON CONFLICT (key, lang) DO NOTHING;
INSERT INTO trad (key, lang, translation) VALUES ('grotte_pins_text', 'en', 'An early example of an artificial grotto in France, the "Pine Grotto" completes the end of the Louis XV wing built in place of the Ulysses Gallery wing.') ON CONFLICT (key, lang) DO NOTHING;
INSERT INTO trad (key, lang, translation) VALUES ('grand_parterre', 'en', 'The Grand Parterre') ON CONFLICT (key, lang) DO NOTHING;
INSERT INTO trad (key, lang, translation) VALUES ('grand_parterre_text', 'en', 'The creation of the Grand Parterre from 1660 to 1664 - the largest in Europe, covering 14 hectares - by André Le Nôtre and Louis Le Vau, reflects Louis XIV''s desire for clarity of space at Fontainebleau.') ON CONFLICT (key, lang) DO NOTHING;
INSERT INTO trad (key, lang, translation) VALUES ('parc', 'en', 'The Park') ON CONFLICT (key, lang) DO NOTHING;
INSERT INTO trad (key, lang, translation) VALUES ('parc_text', 'en', 'Extending beyond the slope of the Cascade Basin, the park formerly marked - to the east - the boundaries of the royal domain. From the village of Avon, it was crossed by the main access road to the castle. Its current configuration, the organization of the network of small cascades and star-shaped alleys, results from the creation, under Henri IV, of a large canal nearly 1200 meters long (1606-1609) and 40 meters wide. Initially planted with over sixty thousand trees including rows of white poplars, oaks, and fruit trees, this "enclosed park" and its canal were the pride of the king, who closely followed its construction. In 1609, it took more than a week to fill it, and from autumn onwards, the king navigated it.') ON CONFLICT (key, lang) DO NOTHING;
INSERT INTO trad (key, lang, translation) VALUES ('cour_offices', 'en', 'The Cour des Offices/Henri IV quarter') ON CONFLICT (key, lang) DO NOTHING;
INSERT INTO trad (key, lang, translation) VALUES ('cour_offices_text', 'en', 'A vast complex formed by three wings in sandstone, bricks, and coated rubble, this forecourt demonstrates Henri IV''s ambition to create a new access to the castle, through the town.') ON CONFLICT (key, lang) DO NOTHING;
INSERT INTO trad (key, lang, translation) VALUES ('porte_baptistere', 'en', 'The Baptistery Gate') ON CONFLICT (key, lang) DO NOTHING;
INSERT INTO trad (key, lang, translation) VALUES ('porte_baptistere_text', 'en', 'It was Henri IV, the great builder king, who erected this triumphal gate topped with a dome around 1606, offering the oval court an opening to the east.') ON CONFLICT (key, lang) DO NOTHING;
INSERT INTO trad (key, lang, translation) VALUES ('horaires_titre', 'en', 'Here are the opening hours:') ON CONFLICT (key, lang) DO NOTHING;
INSERT INTO trad (key, lang, translation) VALUES ('horaires_lien', 'en', 'Link to opening hours') ON CONFLICT (key, lang) DO NOTHING;
INSERT INTO trad (key, lang, translation) VALUES ('titre_principal_jardins', 'es', 'Jardines') ON CONFLICT (key, lang) DO NOTHING;
INSERT INTO trad (key, lang, translation) VALUES ('etang_carpes', 'es', 'El Estanque de las carpas') ON CONFLICT (key, lang) DO NOTHING;
INSERT INTO trad (key, lang, translation) VALUES ('etang_carpes_text', 'es', 'Abriéndose hacia el sur, el estanque de las carpas debe su nombre a las famosas carpas que han estado presentes en Fontainebleau desde Enrique IV.') ON CONFLICT (key, lang) DO NOTHING;
INSERT INTO trad (key, lang, translation) VALUES ('grotte_pins', 'es', 'La gruta de los Pinos') ON CONFLICT (key, lang) DO NOTHING;
INSERT INTO trad (key, lang, translation) VALUES ('grotte_pins_text', 'es', 'Un ejemplo temprano de gruta artificial en Francia, la "Gruta de los Pinos" completa el extremo del ala de Luis XV construida en lugar del ala de la Galería de Ulises.') ON CONFLICT (key, lang) DO NOTHING;
INSERT INTO trad (key, lang, translation) VALUES ('grand_parterre', 'es', 'El Gran Parterre') ON CONFLICT (key, lang) DO NOTHING;
INSERT INTO trad (key, lang, translation) VALUES ('grand_parterre_text', 'es', 'La creación del Gran Parterre de 1660 a 1664 - el más grande de Europa, con sus 14 hectáreas - por André Le Nôtre y Louis Le Vau, refleja el deseo de claridad espacial de Luis XIV en Fontainebleau.') ON CONFLICT (key, lang) DO NOTHING;
INSERT INTO trad (key, lang, translation) VALUES ('parc', 'es', 'El Parque') ON CONFLICT (key, lang) DO NOTHING;
INSERT INTO trad (key, lang, translation) VALUES ('parc_text', 'es', 'Extendido más allá de la pendiente de la Cuenca de las Cascadas, el parque marcaba antiguamente - hacia el este - los límites del dominio real. Desde el pueblo de Avon, era atravesado por la carretera principal de acceso al castillo. Su configuración actual, la organización de la red de pequeñas cascadas y calles en forma de estrella, se debe a la creación, bajo Enrique IV, de un gran canal de casi 1200 metros de largo (1606-1609) y 40 metros de ancho. Inicialmente plantado con más de sesenta mil árboles incluyendo filas de álamos blancos, robles y árboles frutales, este "parque cerrado" y su canal eran motivo de orgullo para el rey, quien siguió de cerca su construcción. En 1609, llevó más de una semana llenarlo y, desde el otoño, el rey lo navegaba.') ON CONFLICT (key, lang) DO NOTHING;
INSERT INTO trad (key, lang, translation) VALUES ('cour_offices', 'es', 'El patio de los Oficios/barrio de Enrique IV') ON CONFLICT (key, lang) DO NOTHING;
INSERT INTO trad (key, lang, translation) VALUES ('cour_offices_text', 'es', 'Un vasto conjunto formado por tres alas de piedra arenisca, ladrillos y escombros revestidos, este patio delantero demuestra la ambición de Enrique IV de crear un nuevo acceso al castillo, a través de la ciudad.') ON CONFLICT (key, lang) DO NOTHING;
INSERT INTO trad (key, lang, translation) VALUES ('porte_baptistere', 'es', 'La puerta del Bautisterio') ON CONFLICT (key, lang) DO NOTHING;
INSERT INTO trad (key, lang, translation) VALUES ('porte_baptistere_text', 'es', 'Fue Enrique IV, el gran rey constructor, quien erigió esta puerta triunfal coronada por una cúpula alrededor de 1606, ofreciendo a la corte ovalada una abertura hacia el este.') ON CONFLICT (key, lang) DO NOTHING;
INSERT INTO trad (key, lang, translation) VALUES ('horaires_titre', 'es', 'Aquí están los horarios:') ON CONFLICT (key, lang) DO NOTHING;
INSERT INTO trad (key, lang, translation) VALUES ('horaires_lien', 'es', 'Enlace a los horarios de apertura') ON CONFLICT (key, lang) DO NOTHING;
